example/post
, HttpMethods.POST)]
    public IActionResult Post() {
        var request = HttpManager.CurrentContext.Request;
        var paramInUrl = request.GetQueryValue(
paramInUrl
);
        var paramInBody = request.GetFormValue(
paramInBody
);
        var name = request.Get
string
(
name
);
        var age = request.Get
int
(
age
