var   uow   =   Application . Ioc . Resolve IUnitOfWork ();  var   filter   =   new   OwnerFilter ();  using   ( uow . Scope ())  using   ( uow . EnableQueryFilter ( filter ))  using   ( uow . EnableOperationFilter ( filter ))   { 
     // 在这里只能查询属于自己的数据 
